public class Rectangle {    private final double width;    private final double length;    public Rectangle(double width, double length) {       width = (width > 0) ? width : 0;       length = (length > 0) ? length : 0;        this.width = width;        this.length = length;    }    public double getWidth() {        return width;    }    public double getLength() {        return length;    }    public double getArea() {        return width * length;    }}